-- ----------------------------
-- 创建税率表
-- ----------------------------
DROP TABLE IF EXISTS `xx_tax_rates`;
CREATE TABLE `xx_tax_rate` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `create_date` datetime NOT NULL,
  `modify_date` datetime NOT NULL,
  `version` bigint(20) NOT NULL,
  `hs_code` varchar(20) NOT NULL COMMENT 'HSCode',
  `categrory` varchar(50) NOT NULL COMMENT '商品种类',
  `tariff_rate` decimal(11,4) DEFAULT '0.0000' COMMENT '关税税率',
  `consumption_tax_rate` decimal(11,4) DEFAULT '0.0000' COMMENT '消费税税率',
  `vat_rate` decimal(11,4) DEFAULT '0.0000' COMMENT '增值税税率',
  `comprehensive_tax_rate` decimal(11,4) DEFAULT '0.0000' COMMENT '综合税税率',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_tax_hs_code` (`hs_code`),
	KEY `ind_tax_hs_code` (`hs_code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='税率';

-- ----------------------------
-- 创建商品和税率关联表
-- ----------------------------
DROP TABLE IF EXISTS `xx_goods_taxrate`;
create table `xx_goods_taxrate`(
  `goods` bigint(20) NOT NULL COMMENT '商品ID',
  `tax_rate` bigint(20) NOT NULL COMMENT '税率ID',
  UNIQUE KEY `uk_goods_taxrate_goods` (`goods`),
	KEY `ind_goods_taxrate_goods` (`goods`),
  KEY `ind_goods_taxrate_taxrates` (`tax_rate`),
  CONSTRAINT `fk_goods_taxrates_goods` FOREIGN KEY (`goods`) REFERENCES `xx_goods` (`id`),
  CONSTRAINT `fk_goods_taxrates_taxrates` FOREIGN KEY (`tax_rate`) REFERENCES `xx_tax_rate` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品和税率关联表';


